import React, { useState, useEffect } from "react";
import styles from "../styles/Product.module.scss";
import { deleteProduct, editProduct } from "../js/buttonEvents.js";

function Product(props) {
    var base64Strings = props.images;
    const [images, setImages] = useState([]);

    useEffect(() => {
        const newImages = base64Strings.map(base64String => "data:image/png;base64," + base64String);
        setImages(newImages);
    }, [base64Strings]);

    useEffect(() => {
        
    }, [images]); //


    function displayImages() {
        return images.map((image, index) => {
            if (image) { // Check if the image src is not null or undefined
                return <img key={index} src={image} onError={(e)=>{e.target.onerror = null; e.target.src="path/to/default/image.png"}}></img>
            }
        });
    }

    return (
        <>
            <div className={styles.Product}>
                <p>{props.name}</p>
                <p>{props.description}</p>
                <p>${props.price}</p>
                <button className={styles.editButton} onClick={() => window.location.href = `/edit/${props.Id}`}>Edit</button>
                <button className={styles.deleteButton} onClick={() => deleteProduct(props.Id, props.updateProducts)}>Delete</button>
                <div className={styles.ImagesContainer}>
                    {displayImages()}
                </div>
            </div>
        </>
    );
}

export default Product;
